.timeline::before {
  position: absolute;
  content: "";
  top: 4px;
  left: 12px;
  width: 1px;
  height: 100%;
  background-color: #f1f1f1;
}
.timeline__item {
  position: relative;
  padding-left: 40px;
}
.timeline__item::before {
  position: absolute;
  content: "";
  top: 3px;
  left: 4px;
  height: 16px;
  width: 16px;
  z-index: 3;
  background-color: #f1f1f1;
  border-radius: 50%;
}
.timeline__item:last-child::after {
  content: "";
  position: absolute;
  z-index: 2;
  width: 1px;
  height: 97%;
  left: 12px;
  bottom: 0;
  background-color: #fbfbfb;
}
.timeline__title {
  font-family: "Plus Jakarta Sans", sans-serif;
  font-weight: 500;
  line-height: 1.25;
}
.timeline__title:not(:last-child) {
  margin-bottom: 10px;
}
.timeline__text {
  font-size: 0.875em;
  line-height: 1.21;
  color: #656d72;
}
.timeline__text:not(:last-child) {
  margin-bottom: 24px;
}
.timeline__date {
  font-weight: 500;
  font-size: 0.875em;
  line-height: 1.21;
}

.content-skills__text:not(:last-child) {
  margin-bottom: 64px;
}
@media (max-width: 767.98px) {
  .content-skills__text:not(:last-child) {
    margin-bottom: 25px;
  }
}
.content-skills__items {
  display: grid;
  gap: 40px;
}
@media (max-width: 767.98px) {
  .content-skills__items {
    gap: 20px;
  }
}
.content-skills__label {
  font-weight: 500;
  font-size: 0.875em;
  line-height: 1.21;
}
.content-skills__label:not(:last-child) {
  margin-bottom: 16px;
}
.content-skills__line {
  background-color: #f5f3fe;
  border-radius: 4px;
  height: 8px;
  position: relative;
}
.content-skills__line span {
  position: absolute;
  height: 100%;
  top: 0;
  left: 0;
  border-radius: 4px;
  background-color: #7e74f1;
}

.contacts {
  background-color: #fbfbfb;
}
.contacts__container {
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding-top: 80px;
  padding-bottom: 80px;
}
@media (max-width: 767.98px) {
  .contacts__container {
    padding-top: 30px;
    padding-bottom: 20px;
  }
}
.contacts__content {
  display: flex;
  align-items: center;
}
@media (max-width: 850px) {
  .contacts__content {
    flex-direction: column-reverse;
  }
}
.contacts__form {
  flex: 0 1 50%;
}
.contacts__form._sending {
  position: relative;
}
.contacts__form._sending::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 1;
  visibility: visible;
  background: rgba(51, 51, 51, 0.3) url(/43c8ce8e44a2b6e2a09947b7a29fa220.gif) center/50px no-repeat;
  transition: all 0.3s ease 0s;
}
@media (max-width: 850px) {
  .contacts__form {
    flex: 0 0 auto;
    width: 100%;
  }
}
.contacts__body {
  flex: 0 1 50%;
}
@media (max-width: 850px) {
  .contacts__body {
    flex: 0 0 auto;
    width: 100%;
    margin-bottom: 40px;
  }
}
.contacts__list {
