}
.form__input:focus::placeholder {
  color: #232e35;
}
.form__button {
  background-color: #7e74f1;
  padding: 16px;
  color: #ffffff;
  border-radius: 8px;
  width: 100%;
  transition: background-color 0.3s ease 0s;
}
.form__button:hover {
  background-color: #574ebc;
}

textarea.form__input {
