.main-section {
  background-color: $white-bg;
  // .main-section__container
  &__container {
    display: grid;
    min-height: 100vh;
    align-items: center;
    grid-template-columns: repeat(2, 1fr);
    padding-top: 160px;
    padding-bottom: 30px;
    column-gap: 20px;

    @media (max-width: $mobile) {
      grid-template-columns: 1fr;
      row-gap: 30px;
      padding-top: 100px;
    }
  }
  // .main-section__content
  &__content {
  }
  // .main-section__title
  &__title {
    &:not(:last-child) {
      margin-bottom: 32px;
    }
  }
  // .main-section__text
  &__text {
    &:not(:last-child) {
      margin-bottom: 40px;
    }
  }
  // .main-section__decor
  &__decor {
  }
  // .main-section__image
  &__image {
  }
}

.decor-main-section {
  max-width: 336px;
  margin: 0px 25px;
  @media (min-width: $mobile) {
    width: 100%;
    justify-self: end;
  }
  // .decor-main-section__box
  &__box {
    position: relative;
    padding-bottom: 119%;
    &::before,
    &::after {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      border-radius: 16px;
    }
    &::before {
      z-index: 1;
      background-color: #7e74f1;
      transform: rotate(-9.55deg);
    }
    &::after {
      z-index: 2;
      background: #eae6fe url(/assets/main/lines.svg) top right / 58% no-repeat;
    }
  }

  // .decor-main-section__image
  &__image {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    border-radius: 16px;
    overflow: hidden;
  }
  // .decor-main-section__picture
  &__picture {
    z-index: 3;
    position: absolute;
    bottom: 0;
    left: 0;
    max-width: 100%;
  }
}

