// ======================================================
.timeline {
  display: grid;
  gap: 64px;
  position: relative;
  @media (max-width: $mobile) {
    gap: 30px;
  }

  &::before {
    position: absolute;
    content: "";
    top: 4px;
    left: 12px;
    width: 1px;
    height: 100%;
    background-color: #f1f1f1;
  }
  // .timeline__item
  &__item {
    position: relative;
    padding-left: 40px;
    &::before {
      position: absolute;
      content: "";
      top: 3px;
      left: 4px;
      height: 16px;
      width: 16px;
      z-index: 3;
      background-color: #f1f1f1;
      border-radius: 50%;
    }
    &:last-child {
      &::after {
        content: "";
        position: absolute;
        z-index: 2;
        width: 1px;
        height: 97%;
        left: 12px;
        bottom: 0;
        background-color: $secondary-bg;
      }
    }
  }
  // .timeline__title
  &__title {
    font-family: "Plus Jakarta Sans", sans-serif;
    font-weight: 500;
    line-height: 1.25;
    &:not(:last-child) {
      margin-bottom: 10px;
    }
  }
  // .timeline__text
  &__text {
    font-size: 0.875em;
    line-height: 1.21;
    color: $greyColor;
    &:not(:last-child) {
      margin-bottom: 24px;
    }
  }
  // .timeline__date
  &__date {
    font-weight: 500;
    font-size: 0.875em;
    line-height: 1.21;
  }
}
