      flex-direction: column-reverse;
    }
  }
  // .contacts__form
  &__form {
    flex: 0 1 50%;
    &::after {
    }
    &._sending {
      position: relative;
    }
    &._sending::after {
      content: "";
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      opacity: 1;
      visibility: visible;
      background: rgba(51, 51, 51, 0.3) url(/fontsicon/loading.gif) center /
        50px no-repeat;
      transition: all 0.3s ease 0s;
    }
    @media (max-width: 850px) {
      flex: 0 0 auto;
      width: 100%;
    }
  }
  // .contacts__body
  &__body {
    flex: 0 1 50%;
    @media (max-width: 850px) {
      flex: 0 0 auto;
      width: 100%;
      margin-bottom: 40px;
    }
  }
